import { Component, Vue, Prop } from 'vue-property-decorator';
import { State, Action } from 'vuex-class';

@Component
export default class ConditionOther extends Vue {
  private sex: any[] = [
    {key: 'f', value: '允许女生'},
    {key: 'm', value: '允许男生'},
  ];
  private type: any[] = [
    '室友合租',
    '转租房源',
    '业主出租',
    '长租公寓',
  ];
  private money: any[] = [
    {value: '1500元以下', min: '', max: 1500},
    {value: '1500-2500元', min: 1500, max: 2500},
    {value: '2500-4000元', min: 2500, max: 4000},
    {value: '4000-6000元', min: 4000, max: 6000},
    {value: '6000-8000元', min: 6000, max: 8000},
    {value: '8000-10000元', min: 8000, max: 10000},
    {value: '10000元以上', min: 10000, max: ''},
  ];
  private conditionNum: number = 0; // 搜索条件的数量
  private label: string = '更多筛选';
  private condition: any = { // 搜索条件
    sex: '',
    LeasePeriod: false,
    type: [],
    money: '',
  };
  @Prop({default: false}) private searchByOther!: boolean;
  @Prop({default: {}}) private openOtherPopup!: any;
  @Prop({default: {}}) private closeOtherPopup!: any;
  @Prop({default: {}}) private changRoomSex!: any;
  @Prop({default: {}}) private changeHasShortRent!: any;
  @Prop({default: {}}) private changeRoomTypeAffirm!: any;
  @Prop({default: {}}) private changeRoomMoney!: any;
  @Prop({default: {}}) private requestCallback!: any;
  private toggleOtherPopup(): void {
    this.conditionNum = 0;
    if (this.searchByOther) {
      this.closeOtherPopup();
    } else {
      this.openOtherPopup();
    }
  }
  // 改变性别
  private changeSex(i: number): void {
    if (this.condition.sex === i) { // 如果点击同一个条件按钮
      this.condition.sex = '';
    } else {
      this.condition.sex = i;
    }
  }
  // 改变租期
  private changeLeasePeriod(bool: boolean): void {
    this.condition.LeasePeriod = !this.condition.LeasePeriod;
  }
  // 改变房源类型
  private changeType(n: any): void {
    const index: number = this.condition.type.indexOf(n);
    if (index > -1) { // 如果点击选择过的条件
      this.condition.type.splice(index, 1);
    } else {
      this.condition.type.push(n);
    }
  }
  // 改变租金
  private changMoney(i: number): void {
    if (this.condition.money === i) { // 如果点击同一个条件按钮
      this.condition.money = '';
    } else {
      this.condition.money = i;
    }
  }
  // 清除
  private clearAll(): void {
    const condition = {
      sex: '',
      LeasePeriod: false,
      type: [],
      money: '',
    };
    this.condition = Object.assign({}, this.condition, condition);
    this.conditionNum = 0;
  }
  // 确定
  private enter(): void {
    if (this.condition.sex !== '') {
      this.conditionNum++;
    }
    if (this.condition.LeasePeriod) {
      this.conditionNum++;
    }
    if (this.condition.type.length > 0) {
      this.conditionNum++;
    }
    if (this.condition.money !== '') {
      this.conditionNum++;
    }
    if (this.conditionNum === 0) {
      this.label = '更多筛选';
    } else {
      this.label = `${this.conditionNum}项条件`;
    }
    this.changRoomSex(this.condition.sex);
    this.changeHasShortRent(this.condition.LeasePeriod);
    this.changeRoomTypeAffirm(this.condition.type);
    this.changeRoomMoney(this.money[this.condition.money]);
    this.closeOtherPopup();
    this.requestCallback();
  }
}
