import { Component, Vue, Watch } from 'vue-property-decorator';
import { State, Action } from 'vuex-class';
import Popup from '@/components/common/popup.vue';
import SearchContentInput from '@/components/search/input.vue';
import SearchFindList from '@/components/search/find_list.vue';
import InputSearchList from '@/components/search/input_search_list.vue';

@Component({
  components: {
    Popup,
    SearchContentInput,
    SearchFindList,
    InputSearchList,
  },
})
export default class SearchInput extends Vue {
  private regions: any[] = [];
  private lines: any[] = [];
  @State((state: any) => state.searchByInput) private searchByInput!: boolean;
  @State((state: any) => state.currentCity) private currentCity!: boolean;
  @State((state: any) => state.initRegions) private initRegions!: any[];
  @State((state: any) => state.initLines) private initLines!: any[];
  @State((state: any) => state.currentRegion) private currentRegion!: string;
  @State((state: any) => state.subwayLine) private subwayLine!: string;
  @State((state: any) => state.locateAddress) private locateAddress!: string;
  @State((state: any) => state.inputSearchList) private inputSearchList!: string;
  @State((state: any) => state.openInputSearchList) private openInputSearchList!: string;
  @Action('openInputPopup') private openInputPopup!: any;
  @Action('closeInputPopup') private closeInputPopup!: any;
  @Action('changeCurrentRegion') private changeCurrentRegion!: any;
  @Action('changeSubwayLine') private changeSubwayLine!: any;
  @Action('searchRoom') private searchRoom!: any;
  @Action('changeRoomHasNextPage') private changeRoomHasNextPage!: any;
  @Action('changeInputSearchList') private changeInputSearchList!: any;
  @Action('openInputSearchListBox') private openInputSearchListBox!: any;
  @Action('changeLongitudeAndLatitude') private changeLongitudeAndLatitude!: any;
  @Watch('currentCity') private change(): void {
    this.changeData();
  }
  @Watch('initRegions') private changeRegions(): void {
    this.changeData();
  }
  private cancleLimit(): void {
    this.changeCurrentRegion('');
    this.changeSubwayLine('');
    this.requestRefreshCallback();
  }
  private changeData(): void {
    this.regions = [];
    this.lines = [];
    if (this.initRegions.length > 0 && this.initLines.length > 0) {
      let regions: any;
      this.initRegions.forEach((item) => {
        if (item.city === this.currentCity) {
          regions = item.data.map((n: any) => {
            return n.name;
          });
        }
      });
      this.regions = regions;
      this.initLines.forEach((item) => {
        if (item.city === this.currentCity) {
          this.lines = item.lines;
        }
      });
    }
  }
  private requestRefreshCallback(): void {
    this.changeRoomHasNextPage(true);
    this.searchRoom({
      isMore: false,
    });
  }
}
